package Practice;

public class Forloopalphabets {
		//Alphabest from a to z
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//displaying message to user
		System.out.println("Alphabest from a to z are:");
		
		for (char i='a';i<='z';i++)
			System.out.println(i);
	}

}
